'use strict';

var request = require('request');
var https = require('https');

var httpsAgent = new https.Agent({ keepAlive: true });

var url = process.argv[2];

var onMessage = function onMessage(message) {
	request({
		method: 'POST',
		url: url,
		followRedirect: true,
		strictSSL: false,
		pool: httpsAgent,
		body: message
	});
};

process.on('message', onMessage);